public class DataTypes {

	//Main method
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int h=98;
		float t = 89.03f;
		double f = 45.44;
		long l = 0x0FL;
		
		//Printing datatypes on console
		System.out.println("Value of Integer is::"+h+"\nValue of Float is::"+t+"\nValue of Double is::"+f+"\nValue of Long is::"+l);
	}

}
